$(".just-for-iosSecond").click(function(){
        	if($(".just-for-ios-conSecond").css("display")=="block"){
                $(".just-for-ios-conSecond").hide();
            }else{
            	var tempSmile=[];
                for(var i=0;i<smileData1.length;i++){
                    tempSmile.push('<div class="ios-smileSecond" data-code="'+smileData1[i].code+'"><img src="'+ctx+'/static/dmp/images/create/'+smileData1[i].img+'" /></div>')
                }
                $(".just-for-conSecond").html(tempSmile.join(""));
                
            	$(".just-for-ios-conSecond").show();
                setTimeout(function(){
                    $(window).on("click",function(e){
                        if($(e.target).parents(".just-for-ios-conSecond").length){

                        }else{
                            $(".just-for-ios-conSecond").hide();
                            $(window).off("click");
                        }
                    });
                },100);
            }
        });
