//预览模式
$("#viewbtnSecond").click(function () {
    $("#codeconSecond").hide().next().show();
    $("#codebtnSecond").removeClass("active").next().addClass("active");
    document.getElementById("viewdivSecond").innerHTML = document.getElementById("templatecontentSecond").value;
    var tempSmile= "";
    var sm = $("#select-multi2").text();
    var bl1 = sm.indexOf("iPad") != -1 || sm.indexOf("iPhone") != -1;
	var bl2 = sm.indexOf("Android") != -1;
	if(document.getElementById("isCsvTag").value == -1 && document.getElementById("transmittingChannel").value == 3) {
		$.post(ctx+"/dmp/tag/checkTagIsFull", {"tagId": document.getElementById("tagId").value, "isCsvTag":document.getElementById("isCsvTag").value, "sign":2}, function (data) {
			if(data == 1 || data == 4 || data == 7) {
				$("#viewdivSecond").css('background-color','#FFF');
		        $("#viewdivSecond").css('color','#555');
		        tempSmile= $("#templatecontentSecond").val()
		        $("#viewdivSecond").html(tempSmile);
			} else if(data == 2) {
				$("#viewdivSecond").css('background-color','#FFF');
	            $("#viewdivSecond").css('color','#555');
	            tempSmile= $("#templatecontentSecond").val().replace(/\[U\w{4}\]/g,function(matched,index,originalText){
	                for(var i=0;i<smileData1.length;i++){
	                    if(smileData1[i].code==matched){
	                        return '<img src="'+ctx+'/static/dmp/images/create/'+smileData1[i].img+'" />';
	                    }
	                }
	            });
	            $("#viewdivSecond").html(tempSmile);
			} else if(data == 3) {
				$("#viewdivSecond").css('background-color',document.getElementById("backgroundColor1Second").value);
	            $("#viewdivSecond").css('color',document.getElementById("textColor1Second").value);
	            tempSmile= $("#templatecontentSecond").val()
	            $("#viewdivSecond").html(tempSmile);
			}
		});
	} else if(document.getElementById("isCsvTag").value == -1 && document.getElementById("transmittingChannel").value != 3) {
		$("#viewdivSecond").css('background-color','#FFF');
        $("#viewdivSecond").css('color','#555');
        tempSmile= $("#templatecontentSecond").val()
	} else {
		if(bl1 && bl2) {
    		//苹果同安卓都有
    		$("#viewdivSecond").css('background-color','#FFF');
            $("#viewdivSecond").css('color','#555');
            tempSmile= $("#templatecontentSecond").val()
    	} else if(bl1) {
    		//苹果
    		$("#viewdivSecond").css('background-color','#FFF');
            $("#viewdivSecond").css('color','#555');
            tempSmile= $("#templatecontentSecond").val().replace(/\[U\w{4}\]/g,function(matched,index,originalText){
                for(var i=0;i<smileData1.length;i++){
                    if(smileData1[i].code==matched){
                        return '<img src="'+ctx+'/static/dmp/images/create/'+smileData1[i].img+'" />';
                    }
                }
            });
    	} else if(bl2) {
    		//安卓
    		$("#viewdivSecond").css('background-color',document.getElementById("backgroundColor1Second").value);
            $("#viewdivSecond").css('color',document.getElementById("textColor1Second").value);
            tempSmile= $("#templatecontentSecond").val()
    	} else {
    		$("#viewdivSecond").css('background-color','#FFF');
            $("#viewdivSecond").css('color','#555');
            tempSmile= $("#templatecontentSecond").val()
    	}
	}
    $("#viewdivSecond").html(tempSmile);
});
